window.onload = function () {
	document.body.classList.add('loaded__hiding');
	window.setTimeout(function () {
		document.body.classList.add('loaded');
		document.body.classList.remove('loaded');
	}, 200);
};
document.addEventListener('DOMContentLoaded', function () {
	gsap.registerPlugin(ScrollTrigger);
	const mm = window.gsap.matchMedia();
	mm.add('(min-width: 501px)', () => {
		ScrollTrigger.create({
			trigger: '.header__wrap',
			start: 'top',
			easy: 'power3.out',
			end: '950%',
			toggleClass: {
				targets: '.left_animation-text, .right_animation-text ',
				className: 'active',
				// markers: true,
			},
		});
		ScrollTrigger.create({
			trigger: '.header__wrap',
			start: '100vw',
			easy: 'power2.out',
			end: '600%',
			toggleClass: { targets: '.cup ', className: 'active' },
			// markers: true,
		});
		ScrollTrigger.create({
			trigger: '.header__wrap',
			start: '201vw',
			easy: 'power1.out',
			end: '400%',
			toggleClass: { targets: '.sandwich ', className: 'active' },
			// markers: true
		});
		const upPhone = gsap.timeline();
		const downPhone = gsap.timeline();
		upPhone.fromTo('.down_phone', 5, { y: '10%' }, { y: '-100%' });
		ScrollTrigger.create({
			animation: upPhone,
			trigger: '.phone__app',
			start: 'top center',
			easy: 'power1.easeIn',
			end: '150% center',
			scrub: true,
		});
		downPhone.fromTo('.up_phone', { y: 0 }, { y: '150%' });
		ScrollTrigger.create({
			animation: downPhone,
			trigger: '.phone__app',
			start: '-=500 top',
			easy: 'power1.easeIn',
			end: '150% top',
			scrub: true,
		});
		const left = gsap.timeline();
		const right = gsap.timeline();
		right.fromTo('.advantage__right ', { x: '-375%' }, { x: '275%' });
		left.fromTo('.advantage__left', { x: '375%' }, { x: '-275%' });
		ScrollTrigger.create({
			animation: right,
			trigger: '.advantage',
			start: 'top center',
			easy: 'power1.easeIn',
			end: 'bottom center',
			scrub: true,
		});
		ScrollTrigger.create({
			animation: left,
			trigger: '.advantage',
			start: 'top center',
			easy: 'power1.easeIn',
			end: 'bottom center',
			scrub: true,
		});
	});
	mm.add('(max-width: 500px)', () => {
		ScrollTrigger.create({
			trigger: '.header__wrap',
			start: 'top',
			easy: 'power2',
			end: '150%',
			toggleClass: {
				targets: '.left_animation-text, .right_animation-text ',
				className: 'active',
			},
		});
		ScrollTrigger.create({
			trigger: '.header__wrap',
			start: '105vw',
			end: '300%',
			toggleClass: { targets: '.cup ', className: 'active' },
			// markers: true
		});
		ScrollTrigger.create({
			trigger: '.header__wrap',
			start: '205vw',
			end: '250%',
			toggleClass: { targets: '.sandwich ', className: 'active' },
			// markers: true
		});
		const upPhone = gsap.timeline();
		const downPhone = gsap.timeline();
		upPhone.fromTo('.down_phone', 2, { y: 0 }, { y: '-200%' });
		ScrollTrigger.create({
			animation: upPhone,
			trigger: '.phone__app',
			start: '-=350 center',
			easy: 'power3',
			end: '100% center',
			scrub: true,
		});
		downPhone.fromTo('.up_phone', 2, { y: 0 }, { y: '-200%' });
		ScrollTrigger.create({
			animation: downPhone,
			trigger: '.phone__app',
			start: '-=350 center',
			easy: 'power3',
			end: '100% center',
			scrub: true,
		});
		const left = gsap.timeline();
		const right = gsap.timeline();
		right.fromTo('.advantage__right', 2, { x: '-250%' }, { x: '250%' });
		left.fromTo('.advantage__left', 2, { x: '250%' }, { x: '-250%' });
		ScrollTrigger.create({
			animation: right,
			trigger: '.advantage',
			start: '-=200 center',
			easy: 'power3',
			end: '200% center',
			scrub: true,
		});
		ScrollTrigger.create({
			animation: left,
			trigger: '.advantage',
			start: '-=200 center',
			easy: 'power3',
			end: '200% center',
			scrub: true,
		});
	});
});
